import { initialSettingState } from '../state/setting.state';
import { ESettingActions } from '../actions/setting.action';
export function settingReducers(state = initialSettingState, action) {
  switch (action.type) {
    case ESettingActions.SET_SETTING:
      {
        const key = Object.keys(action.payload)[0];
        window.localStorage.setItem(`app.settings.${key}`, action.payload[key]);
        return Object.assign(Object.assign({}, state), action.payload);
      }
    default:
      return state;
  }
}
